INSERT INTO `lanpartyclub_item` (`item_id`, `item_name`, `item_brief`, `item_random`) VALUES (2, '蓝派中餐私房菜', '【蓝派】是上海所有轰趴馆中“唯一真正拥有”自己的“五星级名厨”的高端轰趴馆，蓝派中餐私房菜均由蓝派自己的五星级主厨曹大厨现场烹饪，曹大厨曾为成龙、赵本山、陈小春等名星及政要掌厨服务，手艺堪称一绝！法式黑椒安格斯牛肉、捞汁三文鱼、蓝派龙虾帝王卷等，最值得一提是法式黑椒安格斯牛肉，是从英国空运过来的。轰趴体验中“吃的体验”是最为重要的部分，绝对不容忽视！！！', 1);
INSERT INTO `lanpartyclub_item` (`item_id`, `item_name`, `item_brief`, `item_random`) VALUES (3, '凡尔赛宫馆', '蓝派凡尔赛宫馆是目前上海最为奢华的庄园式纯独栋别墅，排名第一！所在的别墅区有很多中国知名影视明星居住，例如冯绍峰、王志文等。蓝派凡尔赛宫馆为纯正法式宫廷风格别墅，建筑面积约900平米，全新豪华装修，更奢侈配有【7个行政大床房】，坐拥约1000平米的奢侈豪装私人花园，并带有气派法式凉亭。', 1);
INSERT INTO `lanpartyclub_item` (`item_id`, `item_name`, `item_brief`, `item_random`) VALUES (4, '威尼斯人馆', '别墅位于顶级别墅区檀宫别墅内。建筑面积约500平米，坐拥约1500平米的奢侈豪装私人花园。更配有高端室内、室外泳池、SPA休息房等。', 0);
INSERT INTO `lanpartyclub_item` (`item_id`, `item_name`, `item_brief`, `item_random`) VALUES (7, '蓝派烧烤自助餐', '蓝派烧烤特色是著名的东北延边烧烤风味，在上海其他地方绝对吃不到！轰趴体验中“吃的体验”是最为重要的部分，绝对不容忽视！！！', 0);
INSERT INTO `lanpartyclub_item` (`item_id`, `item_name`, `item_brief`, `item_random`) VALUES (8, '美国馆', '蓝派美国馆所在的别墅区位于佘山国家旅游度假区，依佘山、北竿山，临淀浦河秀水，拥有四万平方米的主题公园。蓝派美国馆为别墅区中极为稀缺的南加州异国风情顶级奢华庄园级别墅，建筑面积约700平米，坐拥1200平米的奢侈豪装私人花园。', 0);
INSERT INTO `lanpartyclub_item` (`item_id`, `item_name`, `item_brief`, `item_random`) VALUES (9, '法国馆', '蓝派法国馆位于中国一线影视名星的别墅豪宅选择聚集区-上海徐泾，更是大虹桥枢纽核心国际住区。法国馆所在的别墅区有很多中国知名影视明星居住，例如冯绍峰、王志文等。别墅区紧邻著名的银涛高尔夫和国家会展中心。蓝派法国馆为纯正法式宫廷风格别墅，建筑面积约600平米，全新豪华装修。', 0);
INSERT INTO `lanpartyclub_item` (`item_id`, `item_name`, `item_brief`, `item_random`) VALUES (10, '优胜美地馆', '蓝派优胜美地馆位于上海闵行区旗忠高尔夫和上海国际网球中心隔壁。作为亚洲十大豪宅，首屈一指的庄园式独栋别墅尤其令人瞩目，她的美在于大气、在于品质、在于幽雅、在于懂得生活！', 0);
INSERT INTO `lanpartyclub_item` (`item_id`, `item_name`, `item_brief`, `item_random`) VALUES (15, '圣地亚哥馆', '蓝派圣地亚哥馆位于上海闵行区旗忠高尔夫和上海国际网球中心隔壁。作为庄园式独栋别墅，她的美在于大气、在于品质、在于幽雅、在于懂得生活！建筑面积约500平米，1个大床房，2个超大日式榻榻米，3卫，1厨房，3阳台，坐拥约1000平米的奢侈豪装私人花园，并有优美湖畔环绕。', 0);
INSERT INTO `lanpartyclub_item` (`item_id`, `item_name`, `item_brief`, `item_random`) VALUES (16, '白金瀚宫馆', '蓝派白金瀚宫馆位于上海浦东的黄金板块迪士尼区域黄金角，交通便利，半小时内即可到达迪士尼乐园和上海浦东国际机场。会所占地面积5000多平方，建筑面积2800平米。公馆拥有室内恒温游泳池、室外全景露台、中西式餐厅、户外BBQ、咖啡厅、瑜伽室、书吧、健身房、桌游房、台球桌、桌式足球、儿童活动室、室外儿童乐园、KTV、商务会议室、红酒雪茄吧、网球场、篮球场、地下停车场。', 0);
