INSERT INTO `lanpartyclub_item` (`item_id`, `item_name`, `item_brief`, `item_random`) VALUES (64, '卢浮宫·极致奢华馆', '蓝派卢浮宫·极致奢华馆是上海闵行目前“最顶级”的派对轰趴别墅，装修费用近1000万，其所在的别墅区位于闵行区的颛桥版块，是上海最为著名的顶级独栋别墅区之一，众多中国知名企业家与明星皆居住于此。蓝派卢浮宫·极致奢华馆为别墅区中极为稀缺的欧式顶级奢华庄园级别墅，建筑面积约800平米，地上三层，地下一层，坐拥1000平米的奢侈豪装私人花园。', 1);
INSERT INTO `lanpartyclub_item` (`item_id`, `item_name`, `item_brief`, `item_random`) VALUES (65, '夏威夷风情馆', '蓝派夏威夷馆是上海目前唯一“最顶级”的派对轰趴别墅，市价约1个亿，装修费用近1500万，建筑面积约800平米，5房（4个大床房，1个榻榻米房），4厅，4卫，1厨房，坐拥4000平米的奢侈豪装私人花园和露天游泳池。其所在的别墅区位于佘山国家旅游度假区，距佘山仅300米之遥，为上海最顶级的别墅区！', 1);
